import axios from 'axios'
import {useReducer} from 'react'


const formReducer = (state, event) => {
    return {
      ...state,
      [event.target.name]: event.target.value
    }
   }

export default function ItemGridForm({data, setData, sellers}){
    
    const [formData, setFormData] = useReducer(formReducer, {})

    function handleSubmit(e){
        e.preventDefault()
        let newData = data
        sellers.forEach(async (element) => {
            let res = await axios.post("http://localhost:5000/items/getItems", {table:element, pid:formData.pid})
            newData[element] = res.data.rows
            console.log(newData)
            setData(newData)
        });
    }

    return (<>
    
        <form onSubmit={handleSubmit}>
        <fieldset>
          <label>
            PID
            <input name="pid" type="number" onChange={setFormData}/>
          </label>
        </fieldset>
        <button type="submit">Submit</button>        
        </form>    
    
    
    </>)
}
