import ItemGridForm from './ItemGridForm'

export default function itemPrev({ item, initData, setHeader, headerTable, data, setData, sellers }) {

    var selectedItemsInitData = {pid: headerData.pid}
     sellers.forEach((seller)=>{
        selectedItemsInitData = {...selectedItemsInitData, [seller]:null}
     })
     
    const [selectedItems, setSelectedItems] = useState(selectedItemsInitData)

    async function updateMatchesDB(){
        console.log("UPDATING MATCHES")
        console.log(selectedItems)
       let res = await axios.post('http://localhost:5000/items/updateMatches', {ids: selectedItems, matchesDB:matchesDB})
       setSelectedItems(selectedItemsInitData)
       console.log(res)
    }

    function getImages() {
        if (item.image_src != null) {
            if (typeof item.image_src == "Array") {
                return item.images.map((image) => {
                    return <img src={image} alt="img" />
                })
            }else{
                return <img src={item.image_src} alt="img" />
            }
        }
    }

    return (<>
        <div className='itemPrev'>
        <div>{item.title}</div>
        {getImages()}
        <div>{item.tags}</div>
        <div>{item.price}</div>
        <ItemGridForm updateMatchesDB={updateMatchesDB} setSelectedItems={setSelectedItems} initData={initData} setHeader={setHeader} headerTable={headerTable} data={data} setData={setData} sellers={sellers} />
        </div>
    </>)
}
