export default function Item({ seller, setSelectedItems, item }) {

    function getImages() {
        if (item.images != null) {
            return item.images.map((image) => {
                return <img src={image} alt="img" />
            })
        }
    }

    function selectItem() {
        setSelectedItems((prev) => {
            prev[seller] = item.id
            return prev
        })
    }

    return (
        <>
            <div className='itemBorder'>
                <div className="sellerTitle">{item.seller}</div>
                <div className="itemImages">
                    {
                        getImages()
                    }
                </div>
                <div className="itemContent">
                    <div className="itemTitle">{item.title} ${item.price}</div>
                    <div className="itemDesc">{item.description}</div>
                    <div className="itemButton" onClick={selectItem}>THIS IS THE ITEM</div>
                </div>
            </div>
        </>
    )
}
