import axios from 'axios'
import { useReducer } from 'react'


const formReducer = (state, event) => {
  return {
    ...state,
    [event.target.name]: event.target.value
  }
}

export default function ItemGridForm({initData, setData, sellers, headerTable, setHeader }) {

  const [formData, setFormData] = useReducer(formReducer, {})

  async function handleSubmit(e) {
    e.preventDefault()
    if (!(formData.pid === "" || formData.pid === undefined)) {
      let init = initData

      sellers.forEach(async (seller) => {
        let res = await axios.post("http://localhost:5000/items/getItems", { table: seller, pid: formData.pid })
        init[seller] = {data:res.data.rows, seller:seller}
        setData(init)
      });

      let res = await axios.post("http://localhost:5000/items/getItems", {table: headerTable, pid: formData.pid})

      if(res.data.rows[0] !== undefined){
        setHeader(res.data.rows[0])
      }
      else{
        setHeader({pid: null, images:[], title:"", description:"", price:""})
      }
    }
  }

  return (<>

    <form onSubmit={handleSubmit}>
      <fieldset className='gridForm'>
        <label>
          PID
          <input name="pid" type="number" onChange={setFormData} />
        </label>
        <button type="submit">Submit</button>
      </fieldset>
    </form>


  </>)
}
