import Item from './Item'
import ItemGridForm from './ItemGridForm'
import { useState } from 'react'

export default function ItemGrid() {
    const sellers = ["amazon", "petsmart", "chewy"]
    const initData = sellers.forEach((seller) => {
        initData = {...initData, [seller]:[]}
    })
    const [data, setData] = useState(initData)



    return (
        <>
            <ItemGridForm setData={setData} sellers={sellers} />
            <div className='itemGrid'>
                {
                    sellers.map(seller => {
                        return (<div className='itemsCol'>
                            <div className='sellerTitle'>{seller}</div>
                            {
                            data[seller].map((element) => {
                                return (<>
                                    <Item image={element.image} itemTitle={element.itemTitle} itemDesc={element.itemDesc} price={element.price} />

                                </>)
                            })
                            }
                        </div>)
                    })
                }
            </div>
        </>
    )
}
