import Item from './Item'
import { useState } from 'react'
import ItemPrev from './itemPrev'
import axios from 'axios'
import ClearSelection from './ClearSelection'

export default function ItemGrid() {
    const gridWidth = 1600
    const sellers = ["amazontest","chewytest"]
    const headerTable = "a_wiztest"
    const matchesDB = "aw_matches"



    var initData
    sellers.forEach((seller) => {
        initData = {...initData, [seller]:{data:[], seller:""}}
    })
    const [data, setData] = useState(initData)
    const [headerData, setHeader] = useState({pid:null, images:[], title:"", description:"", price:""})

    function createGridColTemplate(columns){
        let template = ""
        for(let i = 0; i < columns; i++){
            template += " " + 100/sellers.length + "% "
        }
        return template
    }
    return (
        <div className='flexContent'>  
            <ItemPrev item={headerData} initData={initData} setHeader={setHeader} headerTable={headerTable} data={data} setData={setData} sellers={sellers}/>
            <div className='itemGrid' style={{gridTemplateColumns:createGridColTemplate(sellers.length), width:gridWidth +"px"}}>
                {
                    sellers.map(seller => {
                        return (<div className='itemsCol'>
                            <div className='sellerTitle'>{seller} items</div>
                            <div className='sellerItems' style={{width:gridWidth/sellers.length}}>
                            <ClearSelection seller={seller} setSelectedItems={setSelectedItems}/>
                            {
                            data[seller].data.map((item) => {
                                 return (<>
                                     <Item item={item} seller={data[seller].seller} setSelectedItems={setSelectedItems}/>
                                 </>)
                             })
                            }
                            </div>
                        </div>)
                    })
                }
            </div>
        </div>
    )
}
