import { useEffect, useRef } from "react"

export default function Item({ seller, setSelectedItems, item, selected }) {

    const itemRef = useRef()

    function getImages() {
        if (item.images != null) {
            return item.images.map((image) => {
                return <img src={image} alt="img" />
            })
        }
    }

    function selectItem() {
        setSelectedItems((prev) => {
            prev[seller] = item.id
            return prev
        })
    }

    useEffect(()=>{
        if(selected)
            itemRef.current.style="background-color:#c5f1ff"
        else
            itemRef.current.style="background-color:white"
    },[selected])

    return (
        <>
            <div className='item' ref={itemRef}>
                <div className="itemImages">
                    {
                        getImages()
                    }
                </div>
                <div className="itemContent">
                    <div className="itemTitle">{item.title} <span className="itemPrice">${item.price}</span></div>
                 
                    <div className="itemDesc">{item.description}</div>
                    <div className="button" onClick={selectItem}>THIS IS THE ITEM</div>
                </div>
            </div>
        </>
    )
}
