import axios from 'axios'
import { useReducer } from 'react'


const formReducer = (state, event) => {
  return {
    ...state,
    [event.target.name]: event.target.value
  }
}

export default function ItemGridForm({ setData, sellers, headerTable }) {

  const [formData, setFormData] = useReducer(formReducer, {})

  async function handleSubmit(e) {
    e.preventDefault()
    if (!(formData.pid == "" || formData.pid == undefined)) {
      let initData
      sellers.forEach((seller) => {
        initData = { ...initData, [seller]: [] }
      })
      sellers.forEach(async (element) => {
        let res = await axios.post("http://localhost:5000/items/getItems", { table: element, pid: formData.pid })
        console.log(res.data.rows)
        initData[element] = res.data.rows
        setData(initData)
      });
      let res = await axios.post("http://localhost:5000/items/getItems", {table: headerTable, pid: formData.pid})
      setHeader(res.data.rows)
    }
  }

  return (<>

    <form onSubmit={handleSubmit}>
      <fieldset>
        <label>
          PID
          <input name="pid" type="number" onChange={setFormData} />
        </label>
      </fieldset>
      <button type="submit">Submit</button>
    </form>


  </>)
}
