import Item from './Item'
import ItemGridForm from './ItemGridForm'
import {useEffect, useState} from 'react'
import axios from 'axios'

export default function ItemGrid(){

    const [data, setData] = useState([{image: "https://m.media-amazon.com/images/I/61AALhMN3lL._AC_SY450_.jpg", itemTitle:"ZESTY PAWS", itemDesc:"THIS IS DESCRIPTION LOL", price:"10.99"}])

    useEffect(()=>{
        getDBData()
    },[data])

    async function getDBData(){
        let res = await axios.post('http://localhost:5000/items/getItems', {pid:1})
        setData(res.data)
    }

    return (
        <> 
        <ItemGridForm />
        <div className='itemGrid'>
        <div className='sellerTitle'>AMAZON</div><div className='sellerTitle'>PETSMART</div><div className='sellerTitle'>CHEWY</div>
        {
        data.map((element) => {
                return (<>
                <Item image={element.image} itemTitle={element.itemTitle} itemDesc={element.itemDesc} price={element.price}/>
                
                </>)
        })
        }
        </div>
        </>
    )
}
