import Item from './Item'
import ItemGridForm from './ItemGridForm'
import { useEffect, useState } from 'react'

export default function ItemGrid() {
    const gridWidth = 1600
    const sellers = ["amazon", "petsmart", "chewy"]
    const headerTable = "animal_wiz"

    var initData
    sellers.forEach((seller) => {
        initData = {...initData, [seller]:[]}
    })
    const [data, setData] = useState(initData)
    const [headerData, setHeader] = useState({images:[], title:"", description:"", price:""})
    
    function createGridColTemplate(columns){
        let template = ""
        for(let i = 0; i < columns; i++){
            template += " " + 100/sellers.length + "% "
        }
        return template
    }

    useEffect(()=>{
        console.log(data)
    }, [data])
    return (
        <>  <div className='headerData'><Item images={headerData.images} itemTitle={headerData.title} itemDesc={headerData.description} price={headerData.price} /></div>
            <ItemGridForm setHeader={setHeader} headerTable={headerTable} data={data} setData={setData} sellers={sellers} />
            <div className='itemGrid' style={{gridTemplateColumns:createGridColTemplate(sellers.length), width:gridWidth +"px"}}>
                {
                    sellers.map(seller => {
                        return (<div className='itemsCol'>
                            <div className='sellerTitle'>{seller}</div>
                            <div className='sellerItems' style={{width:gridWidth/sellers.length}}>
                            {
                            data[seller].map((seller) => {
                                 return (<>
                                     <Item images={seller.images} itemTitle={seller.title} itemDesc={seller.description} price={seller.price} />
                                 </>)
                             })
                            }
                            </div>
                        </div>)
                    })
                }
            </div>
        </>
    )
}
